import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Circle, LayersControl } from 'react-leaflet';
import { useMap } from '../contexts/MapContext';
import { MapLayer, SiteData } from '../contexts/MapContext';
import LayerControlPanel from '../components/Map/LayerControlPanel';
import SiteInfoPanel from '../components/Map/SiteInfoPanel';
import MapToolbar from '../components/Map/MapToolbar';
import HeatmapLegend from '../components/Map/HeatmapLegend';
import { 
  Layers, 
  Eye, 
  EyeOff, 
  Sliders, 
  Info,
  Target,
  Zap
} from 'lucide-react';

// Mock data for demonstration
const mockSites: SiteData[] = [
  {
    id: '1',
    name: 'Jaisalmer Solar Hub',
    coordinates: [26.9117, 70.9228],
    state: 'Rajasthan',
    district: 'Jaisalmer',
    solarIndex: 95,
    windIndex: 65,
    waterIndex: 30,
    industryProximity: 70,
    gridProximity: 85,
    landAvailability: 90,
    suitabilityScore: 88,
    policyZone: 'SEZ',
    existingInfrastructure: ['Solar Plant', 'Grid Connection']
  },
  {
    id: '2',
    name: 'Gujarat Wind Corridor',
    coordinates: [22.2587, 71.1924],
    state: 'Gujarat',
    district: 'Surendranagar',
    solarIndex: 75,
    windIndex: 92,
    waterIndex: 60,
    industryProximity: 85,
    gridProximity: 90,
    landAvailability: 80,
    suitabilityScore: 85,
    policyZone: 'GIFT City',
    existingInfrastructure: ['Wind Farm', 'Port Access']
  },
  {
    id: '3',
    name: 'Tamil Nadu Industrial Zone',
    coordinates: [13.0827, 80.2707],
    state: 'Tamil Nadu',
    district: 'Chennai',
    solarIndex: 70,
    windIndex: 60,
    waterIndex: 85,
    industryProximity: 95,
    gridProximity: 90,
    landAvailability: 50,
    suitabilityScore: 78,
    policyZone: 'SEZ',
    existingInfrastructure: ['Industrial Park', 'Port', 'Airport']
  }
];

const MapView: React.FC = () => {
  const { state, dispatch } = useMap();
  const [showLayerPanel, setShowLayerPanel] = useState(true);
  const [showSitePanel, setShowSitePanel] = useState(false);
  const [selectedSite, setSelectedSite] = useState<SiteData | null>(null);

  const handleSiteClick = (site: SiteData) => {
    setSelectedSite(site);
    setShowSitePanel(true);
    dispatch({ type: 'SELECT_SITE', site });
  };

  const getSuitabilityColor = (score: number) => {
    if (score >= 80) return '#22c55e'; // Green
    if (score >= 60) return '#3b82f6'; // Blue
    if (score >= 40) return '#f59e0b'; // Yellow
    return '#ef4444'; // Red
  };

  const getSuitabilityLabel = (score: number) => {
    if (score >= 80) return 'Excellent';
    if (score >= 60) return 'Good';
    if (score >= 40) return 'Moderate';
    return 'Poor';
  };

  return (
    <div className="h-full flex flex-col">
      {/* Map Header */}
      <div className="bg-white border-b border-gray-200 px-6 py-4">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold text-gray-900">Interactive Map</h1>
            <p className="text-gray-600">Explore India's hydrogen potential with interactive layers and analysis</p>
          </div>
          <div className="flex items-center space-x-3">
            <button
              onClick={() => setShowLayerPanel(!showLayerPanel)}
              className="btn-secondary flex items-center"
            >
              {showLayerPanel ? <EyeOff className="w-4 h-4 mr-2" /> : <Eye className="w-4 h-4 mr-2" />}
              {showLayerPanel ? 'Hide' : 'Show'} Layers
            </button>
            <button
              onClick={() => dispatch({ type: 'TOGGLE_HEATMAP_MODE' })}
              className={`btn-primary flex items-center ${state.heatmapMode ? 'bg-green-600 hover:bg-green-700' : ''}`}
            >
              <Target className="w-4 h-4 mr-2" />
              {state.heatmapMode ? 'Heatmap On' : 'Heatmap Mode'}
            </button>
          </div>
        </div>
      </div>

      {/* Map Container */}
      <div className="flex-1 relative">
        <MapContainer
          center={[23.5937, 78.9629]} // Center of India
          zoom={5}
          className="h-full w-full"
          zoomControl={false}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />

          {/* Render sites as markers */}
          {mockSites.map((site) => (
            <Circle
              key={site.id}
              center={site.coordinates}
              radius={50000} // 50km radius
              pathOptions={{
                color: getSuitabilityColor(site.suitabilityScore),
                fillColor: getSuitabilityColor(site.suitabilityScore),
                fillOpacity: 0.3,
                weight: 2
              }}
              eventHandlers={{
                click: () => handleSiteClick(site)
              }}
            >
              <Popup>
                <div className="p-2">
                  <h3 className="font-semibold text-gray-900 mb-2">{site.name}</h3>
                  <div className="space-y-1 text-sm">
                    <p><strong>State:</strong> {site.state}</p>
                    <p><strong>District:</strong> {site.district}</p>
                    <p><strong>Suitability Score:</strong> 
                      <span className={`ml-2 suitability-score score-${getSuitabilityLabel(site.suitabilityScore).toLowerCase()}`}>
                        {site.suitabilityScore}/100
                      </span>
                    </p>
                    <p><strong>Policy Zone:</strong> {site.policyZone}</p>
                  </div>
                  <button
                    onClick={() => handleSiteClick(site)}
                    className="mt-3 w-full btn-primary text-sm py-1"
                  >
                    View Details
                  </button>
                </div>
              </Popup>
            </Circle>
          ))}

          {/* Heatmap overlay when enabled */}
          {state.heatmapMode && (
            <div className="absolute top-4 right-4 z-[1000]">
              <HeatmapLegend />
            </div>
          )}
        </MapContainer>

        {/* Layer Control Panel */}
        {showLayerPanel && (
          <div className="absolute top-4 left-4 z-[1000]">
            <LayerControlPanel />
          </div>
        )}

        {/* Site Information Panel */}
        {showSitePanel && selectedSite && (
          <div className="absolute top-4 right-4 z-[1000] w-96">
            <SiteInfoPanel 
              site={selectedSite} 
              onClose={() => {
                setShowSitePanel(false);
                setSelectedSite(null);
                dispatch({ type: 'SELECT_SITE', site: null });
              }}
            />
          </div>
        )}

        {/* Map Toolbar */}
        <div className="absolute bottom-4 left-4 z-[1000]">
          <MapToolbar />
        </div>
      </div>
    </div>
  );
};

export default MapView;
